package quicksort;

/**
 *
 * @author lynchcs
 */
public class QuickSort {

  /**
   * @param args the command line arguments
   */
  //private String[] sortValues = {"z", "q", "d", "e", "m", "y", "a", "b", "r", "t"};
  private int[] sortValues = {22,13,4,5,98,123,456,9,5,3,13,56};
  public void sort() {
    if (sortValues != null) {
      quicksort(0, sortValues.length - 1);
    }
  }

  private int moveAbout(int low, int high) {
    int top = high;
    int bottom = low;
    if (sortValues != null) {
      int pivot = sortValues[((high + low)/ 2)];
      System.out.println("pivot="+pivot);
      while (top >= bottom) {
        while (sortValues[bottom] < pivot) {
          bottom++;
        }
        while (sortValues[top] > pivot) {
          top--;
        }
        if (top >= bottom) {
          System.out.println("bottom less than");
          exchange(bottom, top);
          bottom++;
          top--;
        }
      }
    }
    return bottom;
  }

  public void quicksort(int low, int high) {
    System.out.println("low=" + low + " value=" + sortValues[low] + "; high=" + high + "value=" + sortValues[high]);
    printValues();
    int index = moveAbout(low,high);
    if (low < index - 1) {
      quicksort(low, index - 1);
    }

    if (index < high) {
      quicksort(index, high);
    }

  }

  private void exchange(int i, int j) {
    int holder = sortValues[i];
    sortValues[i] = sortValues[j];
    sortValues[j] = holder;
  }

  public void printValues() {
    System.out.print("Begin");
    for (int i = 0; i < sortValues.length; i++) {
      System.out.print(i + " - " + sortValues[i] + ";  ");
    }
    System.out.println("End");
  }

  public static void main(String[] args) {
    QuickSort qs = new QuickSort();
    qs.sort();
    qs.printValues();
    System.out.println("after sort");
  }
}

